import React, {useContext, useState} from 'react';
import {AppContext} from '../context/AppContext';
import {Dropdown} from 'react-bootstrap';

const Currency = () => {

    //Currency list
    const currencyList = [
    {name: "Dollar", symbol: "$"},
    {name: "Pound", symbol: "£"},
    {name: "Euro", symbol: "€"},
    {name: "Ruppee", symbol: "₹"}
    ];

    const {dispatch} = useContext(AppContext);

    const [currency, setCurrency] = useState('');

    const changeCurrency = (currency) => {
        setCurrency(currency);
        dispatch({
            type: 'CHG_CURRENCY',
            payload: currency.symbol
        });
    };

    return (
        <div class='d-inline-flex align-items-center justify-content-between' style={{ height: "80%", borderColor: "red"}}>
            <Dropdown>
                <Dropdown.Toggle variant="success" className="text-white">
                    Currency ({currency.symbol} {currency.name})
                </Dropdown.Toggle>

                <Dropdown.Menu className="border border-success bg-success text-black" hover>
                    {currencyList.map((currency) => (
                        <Dropdown.Item onClick={() => changeCurrency(currency)}>
                            {currency.symbol} {currency.name}
                        </Dropdown.Item>
                    ))}
                </Dropdown.Menu>
            </Dropdown>
        </div>
    );
};

export default Currency;
